mapper.update:
UPDATE  /* updateDetailCode */
	T_PMS015 
SET	
	dtl_cd_nm = #{dtlCdNm},
	dtl_cd_desc = #{dtlCdDesc},
	upt_tm =  NOW(),
	upt_id = #{uptId}
WHERE	
	grp_cd = #{grpCd}
AND	dtl_cd = #{dtlCd}

mapper.delete:
DELETE /* deleteDetailCode */
FROM
	T_PMS015 
WHERE	
	grp_cd = #{grpCd}
	AND	dtl_cd = #{dtlCd}

mapper.select:
SELECT /* selectListDetailCode */ grp_cd AS grpCd, dtl_cd AS dtlCd, dtl_cd_nm AS dtlCdNm, dtl_cd_desc AS dtlCdDesc, rgt_tm AS rgtTm, rgt_id AS rgtId, upt_tm AS uptTm, upt_id AS uptId FROM T_PMS015 WHERE 1 = 1
ORDER BY
grp_cd
${orderElement}
DESC
LIMIT ${start}, ${pageSize}
