mapper.select:
SELECT A.PRJT_CD prjtCd, A.DSTRBT_SEQ_NO dstrbtSeqNo, A.PRJT_NM prjtNm, A.TR_ACCT_NM trAcctNm, A.CSTMR_NM cstmrNm, A.CHGR_NM chgrNm, A.HP_NO hpNo, A.CHGR_HP_NO chgrHpNo, A.CNTRCT_SECT_CD cntrctSectCd, A.CNTRCT_SECT_CD_NM cntrctSectCdNm, A.PRSN_NO prsnNo, A.PRSN_NM prsnNm, A.DSTRBT_SECT_CD dstrbtSectCd, A.DSTRBT_SECT_CD_NM dstrbtSectCdNm, DATE_FORMAT(A.WORK_START_DT,'%Y-%m-%d') workStartDt, DATE_FORMAT(A.WORK_END_DT,'%Y-%m-%d') workEndDt, A.PRSN_MM prsnMm, B.CNTRCT_CD cntrctCd, B.CNTRCT_STATUS_CD cntrctStatusCd, (SELECT DTL_CD_NM FROM T_PMS023 WHERE GRP_CD = '014' AND DTL_CD = B.CNTRCT_STATUS_CD) cntrctStatusCdNm, (SELECT IFNULL(PRJT_END_YN, 'N') FROM T_PMS006 WHERE PRJT_CD=A.PRJT_CD) prjtEndYn FROM ( SELECT T1.PRJT_CD, T2.DSTRBT_SEQ_NO, T1.PRJT_NM, (SELECT TR_ACCT_NM FROM T_PMS001 WHERE TR_ACCT_CD=T1.TR_ACCT_CD) TR_ACCT_NM, T1.CSTMR_NM, (SELECT CHGR_NM FROM T_PMS002 WHERE TR_ACCT_CD=T1.TR_ACCT_CD AND CHGR_SEQ_NO=T1.CAL_CHGR_SEQ_NO) CHGR_NM, (SELECT HP_NO FROM T_PMS002 WHERE TR_ACCT_CD=T1.TR_ACCT_CD AND CHGR_SEQ_NO=T1.CAL_CHGR_SEQ_NO) CHGR_HP_NO, T3.HP_NO, T3.CNTRCT_SECT_CD, (SELECT DTL_CD_NM FROM T_PMS023 WHERE GRP_CD = '004' AND DTL_CD = T3.CNTRCT_SECT_CD) CNTRCT_SECT_CD_NM, T3.PRSN_NO, T3.PRSN_NM, T2.DSTRBT_SECT_CD, (SELECT DTL_CD_NM FROM T_PMS023 WHERE GRP_CD = '011' AND DTL_CD = T2.DSTRBT_SECT_CD) DSTRBT_SECT_CD_NM, T2.WORK_START_DT, T2.WORK_END_DT, T2.PRSN_MM, T2.ORDR_UNIT_CST_AMT, T3.JUMIN_NO1, T3.JUMIN_NO2 FROM T_PMS006 T1 INNER JOIN T_PMS007 T2 INNER JOIN T_PMS003 T3 ON T1.PRJT_CD=T2.PRJT_CD AND T2.PRSN_NO=T3.PRSN_NO WHERE PRJT_CFRM_YN='Y'
AND T1.PRJT_NM LIKE CONCAT('%', #{srchPrjtNm}, '%')
AND T3.CNTRCT_SECT_CD = #{srchCntrctSectCd}
AND T3.PRSN_NM LIKE CONCAT('%', #{srchPrsnNm}, '%')
AND T2.DSTRBT_SECT_CD = #{srchDstrbtSectCd}
) A LEFT OUTER JOIN T_PMS011 B ON (A.PRJT_CD=B.PRJT_CD AND A.PRSN_NO=B.PRSN_NO)
WHERE 1=1
AND B.CNTRCT_STATUS_CD = #{srchCntrctStatusCd}
ORDER BY B.CNTRCT_CD DESC, A.PRJT_CD DESC, A.DSTRBT_SEQ_NO DESC
LIMIT ${start}, ${pageSize}

SELECT COUNT(1) FROM ( SELECT T1.PRJT_CD, T1.PRJT_NM, (SELECT TR_ACCT_NM FROM T_PMS001 WHERE TR_ACCT_CD=T1.TR_ACCT_CD) TR_ACCT_NM, T1.CSTMR_NM, (SELECT CHGR_NM FROM T_PMS002 WHERE TR_ACCT_CD=T1.TR_ACCT_CD AND CHGR_SEQ_NO=T1.CAL_CHGR_SEQ_NO) CHGR_NM, (SELECT HP_NO FROM T_PMS002 WHERE TR_ACCT_CD=T1.TR_ACCT_CD AND CHGR_SEQ_NO=T1.CAL_CHGR_SEQ_NO) CHGR_HP_NO, T3.HP_NO, T3.CNTRCT_SECT_CD, (SELECT DTL_CD_NM FROM T_PMS023 WHERE GRP_CD = '004' AND DTL_CD = T3.CNTRCT_SECT_CD) CNTRCT_SECT_CD_NM, T3.PRSN_NO, T3.PRSN_NM, T2.DSTRBT_SECT_CD, (SELECT DTL_CD_NM FROM T_PMS023 WHERE GRP_CD = '011' AND DTL_CD = T2.DSTRBT_SECT_CD) DSTRBT_SECT_CD_NM, T2.WORK_START_DT, T2.WORK_END_DT, T2.PRSN_MM, T2.ORDR_UNIT_CST_AMT, T3.JUMIN_NO1, T3.JUMIN_NO2 FROM T_PMS006 T1 INNER JOIN T_PMS007 T2 INNER JOIN T_PMS003 T3 ON T1.PRJT_CD=T2.PRJT_CD AND T2.PRSN_NO=T3.PRSN_NO WHERE PRJT_CFRM_YN='Y'
AND T1.PRJT_NM LIKE CONCAT('%', #{srchPrjtNm}, '%')
AND T3.CNTRCT_SECT_CD = #{srchCntrctSectCd}
AND T3.PRSN_NM LIKE CONCAT('%', #{srchPrsnNm}, '%')
AND T2.DSTRBT_SECT_CD = #{srchDstrbtSectCd}
) A LEFT OUTER JOIN T_PMS011 B ON (A.PRJT_CD=B.PRJT_CD AND A.PRSN_NO=B.PRSN_NO) WHERE 1=1
AND B.CNTRCT_STATUS_CD = #{srchCntrctStatusCd}

SELECT CNTRCT_CD cntrctCd, DPST_SEQ_NO dpstSeqNo, DPST_DT dpstDt, DPST_CD dsptCd, RMLRK_DESC rmlrkDesc, SUPPLY_AMT supplyAmt, TAX_AMT taxAmt, (SUPPLY_AMT+TAX_AMT) totAmt, MEMO_DESC memoDesc, DPST_YN dpstYn FROM T_PMS013 WHERE CNTRCT_CD=#{cntrctCd}
AND DPST_CD = #{srchDsptCd}
ORDER BY DPST_DT DESC, DPST_SEQ_NO DESC

mapper.update:
UPDATE T_PMS011
SET CNTRCT_STATUS_CD = #{cntrctStatusCd},
    MEMO_DESC = #{memoDesc0},
    UPT_TM=NOW(),
	UPTR_ID=#{uptrId}
WHERE CNTRCT_CD = #{cntrctCd}
