mapper.update:
UPDATE  /* updatePerson */
	T_PMS003 
SET	
	prsn_no = #{prsnNo}
	,prsn_nm = #{prsnNm}
	,jumin_no1 = #{juminNo1}
	,jumin_no2 = #{juminNo2}
	,sex_cd = #{sexCd}
	,acdmc_cd = #{acdmcCd}
	,work_start_dt = #{workStartDt}
	,cert_cd = #{certCd}
	,rsdnc_addr = #{rsdncAddr}
	,cntrct_sect_cd = #{cntrctSectCd}
	,law_rgt_no1 = #{lawRgtNo1}
	,law_rgt_no2 = #{lawRgtNo2}
	,biz_rgt_no = #{bizRgtNo}
	,law_biz_nm = #{lawBizNm}
	,ceo_nm = #{ceoNm}
	,prsn_email_addr = #{prsnEmailAddr}
	,tel_no = #{telNo}
	,hp_no = #{hpNo}
	,tr_bank_nm = #{trBankNm}
	,tr_acct_no = #{trAcctNo}
	,tr_dpstr_nm = #{trDpstrNm}
	,pay_day_cd = #{payDayCd}
	,memo_desc = #{memoDesc}
	,prfl_atcht_fl_no = #{prflAtchtFlNo}
	,upt_tm =  NOW()
	,uptr_id = #{uptrId}
WHERE	
	prsn_no = #{prsnNo}

mapper.select:
SELECT /* selectCntPerson */ COUNT(1) FROM ( SELECT A.* , B.skill_sect_cd FROM T_PMS003 A left outer join T_PMS004 B on A.prsn_no = B.prsn_no WHERE 1 = 1
GROUP BY A.PRSN_NO
) AA
