mapper.select:
SELECT T1.TR_ACCT_CD trAcctCd, T1.TR_ACCT_NM trAcctNm, T2.PRJT_CD prjtCd, T2.PRJT_NM prjtNm, T2.CSTMR_NM cstmrNm, CONCAT(SUBSTRING(T2.PRJT_CFRM_DT, 1, 4), '-', SUBSTRING(T2.PRJT_CFRM_DT, 5, 2), '-', SUBSTRING(T2.PRJT_CFRM_DT, 7, 2)) prjtCfrmDt, PRJT_CFRM_YN prjtCfrmYn, CONCAT(SUBSTRING(T2.PRJT_END_DT, 1, 4), '-', SUBSTRING(T2.PRJT_END_DT, 5, 2), '-', SUBSTRING(T2.PRJT_END_DT, 7, 2)) prjtEndDt, PRJT_END_YN prjtEndYn, CONCAT(SUBSTRING(T2.WORK_START_DT, 1, 4), '-', SUBSTRING(T2.WORK_START_DT, 5, 2), '-', SUBSTRING(T2.WORK_START_DT, 7, 2)) workStartDt, CONCAT(SUBSTRING(T2.WORK_END_DT, 1, 4), '-', SUBSTRING(T2.WORK_END_DT, 5, 2), '-', SUBSTRING(T2.WORK_END_DT, 7, 2)) workEndDt, (SELECT sum(PRSN_MM) FROM T_PMS007 WHERE PRJT_CD = T2.PRJT_CD) totMm, (SELECT CHGR_NM FROM T_PMS002 WHERE TR_ACCT_CD=T2.TR_ACCT_CD AND CHGR_SEQ_NO = T2.SALES_CHGR_SEQ_NO) salesChgrNm, T2.PRJT_GRND_DESC prjtGrncDesc, (SELECT COUNT(1) FROM T_PMS008 WHERE PRJT_CD=T2.PRJT_CD) prjtCnt FROM T_PMS001 T1, T_PMS006 T2 WHERE T1.TR_ACCT_CD=T2.TR_ACCT_CD
ORDER BY T1.TR_ACCT_NM ASC, T2.WORK_START_DT DESC
LIMIT ${start}, ${pageSize}

mapper.update:
UPDATE T_PMS006
SET PRJT_CFRM_YN='Y',
    PRJT_CFRM_DT=DATE_FORMAT(now(), '%Y%m%d'),
    UPT_TM=NOW(), 
	UPTR_ID=#{uptrId}
WHERE PRJT_CD = #{prjtCd}

UPDATE T_PMS006
SET PRJT_CFRM_YN='N',
    PRJT_CFRM_DT=NULL,
    UPT_TM=NOW(), 
	UPTR_ID=#{uptrId}
WHERE PRJT_CD = #{prjtCd}

UPDATE T_PMS006
SET PRJT_END_YN='Y',
    PRJT_END_DT=DATE_FORMAT(now(), '%Y%m%d'),
    UPT_TM=NOW(), 
	UPTR_ID=#{uptrId}
WHERE PRJT_CD = #{prjtCd}

UPDATE T_PMS006
SET PRJT_END_YN='N',
    PRJT_END_DT=NULL,
    UPT_TM=NOW(), 
	UPTR_ID=#{uptrId}
WHERE PRJT_CD = #{prjtCd}
